import React, { useState, useEffect} from 'react';
import { Route, Switch, Redirect, useHistory } from 'react-router-dom';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import ImagePopup from './ImagePopup';
import api from '../utils/Api';
import {CurrentUserContext} from '../contexts/CurrentUserContext.js';
import {EditProfilePopup} from './EditProfilePopup';
import {EditAvatarPopup} from './EditAvatarPopup';
import {AddPlacePopup} from './AddPlacePopup';
import Login from './Login';
import Register from './Register';
import ProtectedRoute from './ProtectedRoute';
import InfoTooltip from './InfoTooltip';
import * as auth from './Auth.js';

function App() {
    const [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false);
    const [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false);
    const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(false);
    const [selectedCard, setSelectedCard] = useState(null);
    const [currentUser, setCurrentUser] = useState({name: '', about: ''});
    const [cards, setCards] = useState([]);
    const [loggedIn, setLoggedIn] = useState(false);
    const history = useHistory();
    const [isInfoTooltipOpen, setInfoTooltipOpen] = useState(false);

    // useEffect(() => {
    //    tokenCheck()
    // // }, [])

    // useEffect(() => {
    //     if (loggedIn) {
    //         history.push("/singup")
    //     }
    // }, [loggedIn])

    useEffect(() => {
        api
        .getProfileInfo()
        .then((user) => {
            setCurrentUser(user);

        })
        .catch(err => console.log(`Ошибка профиля: ${err}`));

        api
        .getInitialCards()
        .then((res) => {
            setCards(res)

        })
        .catch(err => console.log(`Ошибка при добавлении карточек: ${err}`))
    }, []); 

    useEffect(() => {
        const handleEscClose = (e) => {
          if (e.key === "Escape") {
            closeAllPopups();
          }
        };

        document.addEventListener("keydown", handleEscClose);

        return () => document.removeEventListener("keydown", handleEscClose);

      }, []); 

    const handleRegister = (email, password) => {
        return auth.register(email, password)
        .then(() => {

          history.push('/signin');
        });
    }

    const handleLogin = (email, password) => {
        return auth.authorize(email, password)
        .then(() => {
        //   if (data.jwt){
        //     localStorage.setItem('jwt', data.jwt);
        //     const { user: { username, email } } = data;
        //     const userData = { username, email }
        //     setUserData(userData)
            setLoggedIn({
                loggedIn: true
            });
            history.push('/')
        //   }
        })

    } 

    const handleEditAvatarClick = () => {
        setEditAvatarPopupOpen(true);
    };
    const handleEditProfileClick = () => {
        setEditProfilePopupOpen(true);
    }
    const handleAddPlaceClick= () => {
        setAddPlacePopupOpen(true);
    }
    const handleCardClick = (card) => {
        setSelectedCard(card);
    }
    const handleInfoTooltipClick = () => {
        setEditAvatarPopupOpen(true);
    };

    const closeAllPopups = () => {
        setEditProfilePopupOpen(false);
        setAddPlacePopupOpen(false);
        setEditAvatarPopupOpen(false);
        setSelectedCard(null);
    }

    const handleUpdateUser = (user) => {
        api
        .editProfile(user)
        .then(res => {
            setCurrentUser(res);
            setEditProfilePopupOpen(false);

        })
        .catch(err => console.log(`Ошибка обновления профиля: ${err}`));
    };

    const handleUpdateAvatar = (user) => {
        api
        .avatarProfile(user)
        .then((res) => {
            setCurrentUser(res);
            setEditAvatarPopupOpen(false);
        })
        .catch(err => console.log(`Ошибка аватара: ${err}`));
    };

    const handleAddPlaceSubmit = (user) => {
        api
        .addCard(user)
        .then((res) => {
            setAddPlacePopupOpen(false);
            setCards([res, ...cards]);
        })
        .catch(err => console.log(`Ошибка аватара: ${err}`));
    }

    function handleCardLike(card) {
        // Снова проверяем, есть ли уже лайк на этой карточке
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        // Отправляем запрос в API и получаем обновлённые данные карточки
        api
        .changeLikeCardStatus(card._id, isLiked)
        .then((newCard) => {
            setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
        });
    };

    function handleCardDelete(card) {
        // Снова проверяем, являемся ли мы владельцем текущей карточки
        const isOwn = card.owner._id === currentUser._id;

        // Отправляем запрос в API и получаем обновлённые данные карточки
        api
        .deleteCard(card._id, !isOwn)
        .then(() => {
            setCards((state) => state.filter((c) => c._id != card._id ));

        });
    } 

    function componentCards() {
        return <>
                    <Main
                    onEditAvatar={handleEditAvatarClick}
                    onEditProfile={handleEditProfileClick}
                    onAddPlace={handleAddPlaceClick}
                    onCardClick={handleCardClick}
                    cards={cards}
                    onCardsLike={handleCardLike}
                    onCardDelete={handleCardDelete}
                    />

                    <Footer/>

                    <EditProfilePopup 
                    isOpen={isEditProfilePopupOpen} 
                    onClose={closeAllPopups}
                    onUpdateUser={handleUpdateUser} 
                    /> 

                    <AddPlacePopup 
                    isOpen={isAddPlacePopupOpen} 
                    onClose={closeAllPopups}
                    onAddPlace={handleAddPlaceSubmit} 
                    />

                    <EditAvatarPopup 
                    isOpen={isEditAvatarPopupOpen} 
                    onClose={closeAllPopups}
                    onUpdateAvatar={handleUpdateAvatar} 
                    />

                    <ImagePopup 
                    card = {selectedCard}
                    onClose = {closeAllPopups}
                    />

                </>
    } ;

    return (  
        <CurrentUserContext.Provider value={currentUser}>

            <Switch>

                <ProtectedRoute
                exact path="/"
                loggedIn={loggedIn}
                component={componentCards}
                />

                <Route path="/signin">  
                    <Login  handleLogin={handleLogin}/>
                </Route>

                <Route path="/signup">
                    <Register handleRegister={handleRegister}/>
                    <InfoTooltip 
                    // name= 
                    // img= 
                    // title= 
                    // isOpen=
                    // onClose=
                    />
                </Route>

                {/* <Route exact path='/'>

                    <Main
                    onEditAvatar={handleEditAvatarClick}
                    onEditProfile={handleEditProfileClick}
                    onAddPlace={handleAddPlaceClick}
                    onCardClick={handleCardClick}
                    cards={cards}
                    onCardsLike={handleCardLike}
                    onCardDelete={handleCardDelete}
                    />

                    <Footer/>

                    <EditProfilePopup 
                    isOpen={isEditProfilePopupOpen} 
                    onClose={closeAllPopups}
                    onUpdateUser={handleUpdateUser} 
                    /> 

                    <AddPlacePopup 
                    isOpen={isAddPlacePopupOpen} 
                    onClose={closeAllPopups}
                    onAddPlace={handleAddPlaceSubmit} 
                    />

                    <EditAvatarPopup 
                    isOpen={isEditAvatarPopupOpen} 
                    onClose={closeAllPopups}
                    onUpdateAvatar={handleUpdateAvatar} 
                    />

                    <ImagePopup 
                    card = {selectedCard}
                    onClose = {closeAllPopups}

                    />

                </Route> */}

                <Route exact path="/">
                {loggedIn ? <Redirect to="/" /> : <Redirect to="/signin" />}

                </Route>

            </Switch>
        </CurrentUserContext.Provider>
    );
}

export default App;
