import React, { useContext, useState } from 'react';
import editButton from '../images/edit-button.svg'
import plus from '../images/plus.svg'
import Card from './Card';
import {CurrentUserContext} from '../contexts/CurrentUserContext.js';
import Header from './Header';
import { Route, Switch, Redirect, Link } from 'react-router-dom';
import * as auth from './Auth.js';

function Login({
  handleLogin
   }) 

   {

    const [data, setData] = useState({ 
      email: '', 
      password: ''
    });
    const {email, password} = data;

    function handleSubmit(e){
      e.preventDefault();
    if (!email || !password){
      return;
    }
    auth.authorize(email, password)
    .then((data) => {
      if (data.jwt){
        this.setState({username: '', password: ''} ,() => {
            handleLogin();
            // this.props.history.push('/ducks');
        })
      }  
    })
    .catch(err => console.log(err)); // запускается, если пользователь не найден
  } 

    return (
        <>
        <Header 
        menu='Регистрация'
        link='/signup'
        />

        <main className="content content__form">

        <form className="form">
          <p className="form__header">Вход</p>
          <input required id="email" value={email} onChange name="email" type="email" placeholder='Email' className="form__input"/>
          <input required id="password" name="password" type="password" placeholder='Пароль' className="form__input"/>
          <button type="submit" className="form__button">Войти</button>
        </form>

        </main>
        </>

    )
};

export default Login
