import React, { useState, useEffect, useContext } from 'react';
import PopupWithForm from './PopupWithForm';
import {CurrentUserContext} from '../contexts/CurrentUserContext'; 

export function AddPlacePopup({
    isOpen, 
    onClose,
    onAddPlace
    }) {

        const [name, setName] = useState('');
        const [link, setLink] = useState('');
        const currentUser = useContext(CurrentUserContext);

        useEffect(() => {
            if (currentUser){
            setName(currentUser.name);
            setLink(currentUser.link);

        }

          }, [currentUser]); 

        function handleChange(e) {
            setName(e.target.value);
          };

        function handleChangeLink(e) {
            setLink(e.target.value);
        };

        function handleSubmit(e) {
            // Запрещаем браузеру переходить по адресу формы
            e.preventDefault();

            // Передаём значения управляемых компонентов во внешний обработчик
            onAddPlace({
                name,
                link,

              });

          } 

        return (
            <PopupWithForm
                title='Новое место'
                name='mesto'
                children={
                    <>
                    <input id="nameMesto" value={name || ''} onChange={handleChange} type="text" className="popup__input popup__text popup__text_type_mesto" name="mesto" placeholder="Название" required defaultValue="" minLength="2" maxLength="30"/>
                    <span id="nameMesto-error" className="error"></span>
                    <input id="link" type="url" value={link || ''} onChange={handleChangeLink} className="popup__input popup__text popup__text_type_link" name="link" placeholder="Ссылка на картинку" required defaultValue=""/>
                    <span id="link-error" className="error"></span>
                    </>
                }
                onClose={isOpen}
                isOpen={onClose}
                onSubmit={handleSubmit}

            /> 
        )
    }
